def generate_icons():
    print(f"Generating icons in {ICONS_DIR}...")
    for name, path_data in ICONS.items():
        svg_content = SVG_TEMPLATE.format(color=ICON_COLOR, content=path_data)
        file_path = os.path.join(ICONS_DIR, f"{name}.svg")
        with open(file_path, "w", encoding="utf-8") as f:
            f.write(svg_content)
        print(f"Generated: {name}.svg")

if __name__ == "__main__":
    generate_icons()
